import React, { useState } from "react";
// get form data
const Input = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    cell: "",
    uName: "",
  });

  let { name, email, cell, uName } = form;
  console.log(form);
  // alert management
  const [alert, setAlert] = useState({
    msg: "I form alert",
    type: "danger",
    status: false,
  });

  // alert closing
  const handleAlertClose = () => {
    setAlert({
      msg: "",
      type: "",
      status: false,
    });
  };

  //   form submit
  const handleFromSubmit = (e) => {
    e.preventDefault();
    if (name === "" || email === "" || cell === "" || uName === "") {
      setAlert({
        msg: "All Field are require!",
        type: "danger",
        status: true,
      });
    } else {
      setAlert({
        msg: "Data Stable!",
        type: "success",
        status: true,
      });
      setForm({
        name: "",
        email: "",
        cell: "",
        uName: "",
      });
    }
  };

  return (
    <>
      <div className="container my-5">
        <div className="row">
          <div className="col-md-5 m-auto">
            <div className="card shadow">
              <div className="card-header">
                <h2>New User From</h2>
              </div>
              <div className="card-body">
                <div className="msg">
                  {alert.status && (
                    <p
                      className={`alert alert-${alert.type} d-flex justify-content-between`}
                    >
                      {alert.msg}
                      <button
                        className="btn-close"
                        onClick={handleAlertClose}
                      ></button>
                    </p>
                  )}
                </div>
                <form action="" onSubmit={handleFromSubmit}>
                  <div className="my-3">
                    <label htmlFor="">Name</label>
                    <input
                      type="text"
                      value={name}
                      onChange={(e) =>
                        setForm({ ...form, name: e.target.value })
                      }
                      className="form-control"
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">Email</label>
                    <input
                      type="text"
                      className="form-control"
                      value={email}
                      onChange={(e) =>
                        setForm({ ...form, email: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">Cell</label>
                    <input
                      type="text"
                      className="form-control"
                      value={cell}
                      onChange={(e) =>
                        setForm({ ...form, cell: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">User Name</label>
                    <input
                      type="text"
                      className="form-control"
                      value={uName}
                      onChange={(e) =>
                        setForm({ ...form, uName: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">Gender</label>
                    <input type="radio" id="male" value="male" name="gender" />
                    <label htmlFor="male">Male</label>
                    <input
                      type="radio"
                      id="female"
                      value="female"
                      name="gender"
                    />
                    <label htmlFor="female">Female</label>
                  </div>
                  <div className="my-3">
                    <input
                      type="submit"
                      value="Submit"
                      className="btn btn-success"
                    />
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Input;
