import React, { useState } from "react";
// get form data
const Input = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    cell: "",
    uName: "",
  });

  let { name, email, cell, uName } = form;
  console.log(form);
  // alert management
  const [alert, setAlert] = useState({
    msg: "I form alert",
    type: "danger",
    status: true,
  });
  //   form submit
  const handleFromSubmit = (e) => {
    e.preventDefault();
    if (name === "" || email === "" || cell === "" || uName === "") {
    } else {
    }
  };

  return (
    <>
      <div className="container my-5">
        <div className="row">
          <div className="col-md-5 m-auto">
            <div className="card shadow">
              <div className="card-header">
                <h2>New User From</h2>
              </div>
              <div className="card-body">
                <div className="msg">
                  {alert.status && (
                    <p className="alert alert-danger d-flex justify-content-between">
                      {alert.msg}
                      <button className="btn-close"></button>
                    </p>
                  )}
                </div>
                <form action="" onSubmit={handleFromSubmit}>
                  <div className="my-3">
                    <label htmlFor="">Name</label>
                    <input
                      type="text"
                      value={name}
                      onChange={(e) =>
                        setForm({ ...form, name: e.target.value })
                      }
                      className="form-control"
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">Email</label>
                    <input
                      type="text"
                      className="form-control"
                      value={email}
                      onChange={(e) =>
                        setForm({ ...form, email: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">Cell</label>
                    <input
                      type="text"
                      className="form-control"
                      value={cell}
                      onChange={(e) =>
                        setForm({ ...form, cell: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <label htmlFor="">User Name</label>
                    <input
                      type="text"
                      className="form-control"
                      value={uName}
                      onChange={(e) =>
                        setForm({ ...form, uName: e.target.value })
                      }
                    />
                  </div>
                  <div className="my-3">
                    <input
                      type="submit"
                      value="Submit"
                      className="btn btn-success"
                    />
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Input;
