import React, { useState } from "react";
import {
  Alert,
  Button,
  Card,
  CardImg,
  CloseButton,
  Col,
  Container,
  Modal,
  ModalBody,
  Row,
} from "react-bootstrap";

const Student = () => {
  // alert management
  const [alert, setAlert] = useState({
    msg: "All field are required!",
    type: "danger",
    status: true,
  });
  return (
    <>
      <Container className="my-5">
        <Row>
          <Col md={6} className="m-auto ">
            <Card className="shadow">
              <CardImg src="https://images.unsplash.com/photo-1522071820081-009f0129c71c?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=870&q=80"></CardImg>

              <Card.Body>
                <Card.Title>
                  <h4>we are devs</h4>
                </Card.Title>
                <Card.Text>
                  <p>
                    Lorem, ipsum dolor sit amet consectetur adipisicing elit.
                    Aspernatur maxime, modi optio iste consectetur sapiente
                    minima placeat dignissimos accusantium sequi?
                  </p>
                  {alert.status && (
                    <Alert
                      className="d-flex justify-content-between"
                      variant={alert.type}
                    >
                      {alert.msg}
                      <CloseButton></CloseButton>
                    </Alert>
                  )}
                </Card.Text>
                <Card.Link>Hello</Card.Link>
                <Button variant="info">Click</Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>

      <Modal>
        <ModalBody></ModalBody>
      </Modal>
    </>
  );
};

export default Student;
